package ud05;

public class Test07 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num=0;
		
		// Mientras el numero 0 sea inferior a 100, se imprime y se incrementa en 1.
		while (num<=100){
			System.out.println(num);
			num++;
		}

	}

}
